INSERT INTO `my_bl_post` VALUES(433, 'Export changed files from a git version', 'Create a php script named "export-git.php". Content as follow:\n\n\n    <?php\n    ob_start();\n    passthru(''git show --pretty="format:" --name-only HEAD'');\n    $s = ob_get_clean();\n    $files = explode("\\n", $s);\n    passthru(''git archive -o update.zip HEAD ''.implode('' '', $files));\n    echo "Git version exported. See update.zip";\n\n\nCreate a window batch file to run that script "export.bat". Content as follow:\n\n\n    php export-git.php\n\nRun export.bat in the command line. The zipped file will be generated in the current directory', '2015-09-12 21:53:35', '2015-11-01 14:57:39', 5);
INSERT INTO `my_bl_post` VALUES(434, 'Save file upload  on many fields', 'Controller\r\n```php\r\npublic function actionRegister() {\r\n	$model = new ProAgent(''register'');\r\n	if (isset($_POST[''ProAgent''])) {\r\n		$model->attributes = $_POST[''ProAgent''];\r\n		$model->uploadPhoto=CUploadedFile::getInstance($model,''uploadPhoto'');\r\n		$model->uploadNricFront=CUploadedFile::getInstance($model,''uploadNricFront'');\r\n		$model->uploadNricBack=CUploadedFile::getInstance($model,''uploadNricBack'');\r\n		$model->uploadCertification=CUploadedFile::getInstance($model,''uploadCertification'');\r\n\r\n		if ($model->save()) {\r\n			$model->saveUploadFiles();\r\n		}\r\n	}\r\n	Yii::app()->theme = ''onehome'';\r\n	$this->layout = ''/layouts/onehome/1-col'';\r\n	$this->render(''register'', array(\r\n		''model'' => $model\r\n	));\r\n}\r\n```\r\n\r\nModel\r\n```php\r\npublic function saveUploadFiles() {\r\n	$this->saveUploadField(''uploadPhoto'', ''avatar'');\r\n	$this->saveUploadField(''uploadNricFront'', ''avatar'');\r\n	$this->saveUploadField(''uploadNricBack'', ''avatar'');\r\n	$this->saveUploadField(''uploadCertification'', ''avatar'');\r\n}\r\n\r\npublic function saveUploadField($formFieldName, $dbFieldName) {\r\n	$file = $this->$formFieldName;\r\n	if (!$file) return;\r\n\r\n	$fileName = time() .''-''. (string)$file;\r\n	$savePath = implode(''/'', array(Yii::getPathOfAlias(''webroot''), static::$folderUpload, $this->id, $fileName));\r\n	if (!file_exists(dirname($savePath))) {\r\n		mkdir(dirname($savePath));\r\n	}\r\n\r\n	$file->saveAs($savePath);\r\n	$this->$dbFieldName = $fileName;\r\n	$this->update($dbFieldName);\r\n}\r\n```', '2015-11-02 09:12:47', '2015-11-04 10:41:06', 0);
