INSERT INTO `my_bl_post` VALUES(472, 'Remove registered script file when returning ajax content', 'Code for controller\r\n```php\r\n	/*\r\n	 * Remove registered script file when return ajax content\r\n	 * @author Lam Huynh\r\n	 */\r\n	protected function removeScriptFiles() {\r\n		$cs = Yii::app()->clientScript;\r\n		$rc = new ReflectionClass(''CClientScript'');\r\n		// remove registered script files\r\n		$rp = $rc->getProperty(''scriptFiles'');\r\n		$rp->setAccessible(true);\r\n		$rp->setValue($cs, array());\r\n		\r\n		// remove registered core script files\r\n		$rp2 = $rc->getProperty(''coreScripts'');\r\n		$rp2->setAccessible(true);\r\n		$rp2->setValue($cs, array());\r\n	}\r\n```', '2015-12-14 11:34:09', '2015-12-14 11:34:09', 0);
